import base64
import os

def save_icon(base64_string, filename):
    # Entferne mögliche Leerzeichen und Zeilenumbrüche
    base64_string = base64_string.strip()
    
    # Dekodiere den Base64-String
    binary_data = base64.b64decode(base64_string)
    
    # Speichere die Datei
    with open(filename, 'wb') as f:
        f.write(binary_data)

# Erstelle icons Ordner falls nicht vorhanden
if not os.path.exists('icons'):
    os.makedirs('icons')

# Dictionary mit Dateinamen und Base64-Strings
icons = {
    'refresh.png': '',
    'folder.png': '',
    'save.png': '',
    'delete.png': '',
    'clear.png': '',
    'help.png': '',
    'file.png': ''
}

# Konvertiere und speichere jedes Icon
for filename, base64_string in icons.items():
    filepath = os.path.join('icons', filename)
    save_icon(base64_string, filepath)
    print(f'Icon gespeichert: {filepath}') 